mapper.select:
    select 

    from sys_param_class


    			and param_type_class like concat('%',#{paramTypeClass},'%')


    			and param_type_name like concat('%',#{paramTypeName},'%')


    			and visible = #{visible}


    			and physics_flag = #{physicsFlag}





			order by ${sortField} ${direction}


			order by create_date desc

mapper.insert:
insert into sys_param_class (param_type_class, param_type_name, visible, physics_flag, create_by, create_date, update_by, update_date, remarks)
values (#{paramTypeClass,jdbcType=VARCHAR}, #{paramTypeName,jdbcType=VARCHAR}, #{visible,jdbcType=INTEGER}, #{physicsFlag,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}
	, #{createDate,jdbcType=TIMESTAMP},  #{updateBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{remarks,jdbcType=VARCHAR}
  )
