mapper.select:
select

from sys_param_type

    physics_flag = 1

        and param_type_name like concat('%',#{paramTypeName},'%')


        and param_type_class = #{paramTypeClass}


        and param_type_id = #{paramTypeId}

select
, remarks
from sys_param_type

    physics_flag = 1

        and param_type_name = #{paramTypeName}


        and param_type_class = #{paramTypeClass}


        and param_type_code = #{paramTypeCode}


        and param_type_id = #{paramTypeId}

mapper.insert:
insert into sys_param_type (param_type_id, param_type_class, param_type_code, param_type_name, physics_flag, create_by, create_date, update_by, update_date, remarks)
values (#{paramTypeId,jdbcType=INTEGER}, #{paramTypeClass,jdbcType=VARCHAR}, #{paramTypeCode,jdbcType=VARCHAR}, #{paramTypeName,jdbcType=VARCHAR}
    , #{physicsFlag,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}
    , #{createDate,jdbcType=TIMESTAMP},  #{updateBy,jdbcType=VARCHAR}, #{updateDate,jdbcType=TIMESTAMP}, #{remarks,jdbcType=VARCHAR})
