mapper.select:
select 

from 


          and u.mobile = #{mobile}


          and u.login_name = #{loginName}


          and u.email = #{email}


          and u.fld_n1 = #{fldN1}


          and u.fld_n2 = #{fldN2}


          and u.fld_s1 = #{fldS1}


          and u.fld_s2 = #{fldS2}

select u.login_name, u.id, u.password, salt from sys_user u


        and u.id = #{id}


        and u.login_name = #{loginName}

select

from 


        and u.id = #{id}



            or d.parent_ids like concat('%', #{deptId},'%')


            and d.id = #{deptId}



        and u.status = #{status}


        and u.login_name like concat('%', #{loginName}, '%')


        and u.name like concat('%',#{name},'%')


        and u.mobile like concat('%',#{mobile},'%')


        and u.fld_n1 = #{fldN1}


        and u.fld_n2 = #{fldN2}


        and u.fld_s1 like concat('%', #{fldS1},'%')


        and u.fld_s2 like concat('%', #{fldS2},'%')


        and u.create_by = #{createBy}


        and u.create_date > #{startDate}


        and  u.create_date < #{endDate} 


  order by id desc

mapper.update:
update sys_user set status = #{status}, update_by = #{updateBy}, update_date = #{updateDate}
  where


          id = #{id}


          login_name = #{loginName}


          login_name = ''
